watch.set:
var instance = this;

instance.__vs_instance = new vs.ui.Picker ({
  node: instance._views.$root,
  data: instance._properties.data
}).init ();

instance.__vs_instance.bind ('change', instance.__vs_instance, function (e) {
  flexo.notify (instance, '@change', {data: e.data});
})

vs.util.defineProperty (instance._properties, 'selectedKeys', {
  get : function () {
    return instance.__vs_instance.selectedKeys;
  }
})

vs.util.defineProperty (instance._properties, 'selectedValues', {
  get : function () {
    return instance.__vs_instance.selectedValues;
  }
})
