watch.set:
var instance = this;

instance.__vs_instance = new vs.ui.RadioButton ({
  node: instance._views.$root,
  data: instance._properties.data
}).init ();

instance.__vs_instance.bind ('change', instance.__vs_instance, function (e) {
  flexo.notify (instance, '@change', {data: e.data});
})

vs.util.defineProperty (instance, 'model', {
  set : function (v) {
    instance.__vs_instance.model = v;
  }
})
