watch.set:
var instance = this;

instance.__vs_instance = new vs.ui.RadioButton ({
  node: instance.views.$root,
  data: instance.properties.data
}).init ();

vs.util.extendsBenderInstance (instance, instance.__vs_instance);

instance.__vs_instance.bind ('change', instance.__vs_instance, function (e) {
  instance.properties.selectedIndex = instance.__vs_instance._selected_index;
  flexo.notify (instance, '@change', {data: e.data});
})

vs.util.defineProperty (instance, 'model', {
  set : function (v) {
    instance.__vs_instance.model = v;
  }
})
